@section('content')
    <!-- Main content -->
    <div class="content">
        <div class="container-fluid">
            <div class="row">
                <!-- /.col-md-6 -->
                <div class="col-lg-6">
                    <!-- Pesan Berhasil / Error saat proses scan QR -->

                    <div class="card">
                        <div class="card-header">
                            <h5 class="m-0">Menu Scan QR</h5>
                        </div>
                        <div class="card-body">
                            <p class="card-text">Pilih kamera belakang pada kolom di bawah, kemudian arahkan ke kode QR pada
                                kartu siswa.</p>
                            <section class="section">
                                <div class="card mb-" style="max-width: 100%;">
                                    <div class="row no-gutters">
                                        <div class="col-lg-5">
                                            <div class="card-body">

                                                <video id="previewKamera"
                                                    style="max-width: 800px;max-height: 300px;float:center;"></video>
                                                <br>
                                                <select id="pilihKamera" style="max-width:400px">
                                                </select>

                                                <script type="text/javascript" src="https://unpkg.com/@zxing/library@latest"></script>
                                                <script src="https://code.jquery.com/jquery-3.5.1.min.js"
                                                    integrity="sha256-9/aliU8dGd2tb6OSsuzixeV4y/faTqgFtohetphbbj0=" crossorigin="anonymous"></script>

                                                <script>
                                                    let selectedDeviceId = null;
                                                    const codeReader = new ZXing.BrowserMultiFormatReader();
                                                    const sourceSelect = $("#pilihKamera");

                                                    $(document).on('change', '#pilihKamera', function() {
                                                        selectedDeviceId = $(this).val();
                                                        if (codeReader) {
                                                            codeReader.reset()
                                                            initScanner()
                                                        }
                                                    })

                                                    function initScanner() {
                                                        codeReader
                                                            .listVideoInputDevices()
                                                            .then(videoInputDevices => {
                                                                videoInputDevices.forEach(device =>
                                                                    console.log(`${device.label}, ${device.deviceId}`)
                                                                );

                                                                if (videoInputDevices.length > 0) {

                                                                    if (selectedDeviceId == null) {
                                                                        if (videoInputDevices.length > 1) {
                                                                            selectedDeviceId = videoInputDevices[0].deviceId
                                                                        } else {
                                                                            selectedDeviceId = videoInputDevices[1].deviceId
                                                                        }
                                                                    }


                                                                    if (videoInputDevices.length >= 1) {
                                                                        sourceSelect.html('');
                                                                        videoInputDevices.forEach((element) => {
                                                                            const sourceOption = document.createElement('option')
                                                                            sourceOption.text = element.label
                                                                            sourceOption.value = element.deviceId
                                                                            if (element.deviceId == selectedDeviceId) {
                                                                                sourceOption.selected = 'selected';
                                                                            }
                                                                            sourceSelect.append(sourceOption)
                                                                        })

                                                                    }

                                                                    function decodeQRCode() {
                                                                        codeReader
                                                                            .decodeOnceFromVideoDevice(selectedDeviceId, 'previewKamera')
                                                                            .then(result => {
                                                                                console.log(result.text);
                                                                                $("#hasilscan").val(result.text);
                                                                                var snd = new Audio(
                                                                                    "data:audio/mpeg;base64,
                                                                                );
                                                                                snd.play();
                                                                                // Lakukan permintaan AJAX setelah pemindaian berhasil
                                                                                $.ajax({
                                                                                    url: '/scan-qr/' + encodeURIComponent(result.text),
                                                                                    method: 'get',
                                                                                    success: function(response) {
                                                                                        // Aksi setelah permintaan AJAX berhasil
                                                                                        var successMessage = document.createElement("div");
                                                                                        successMessage.className = "alert alert-success";
                                                                                        successMessage.textContent = "Absensi berhasil.";
                                                                                        document.getElementById("messageContainer").appendChild(
                                                                                            successMessage);
                                                                                        setTimeout(function() {
                                                                                            successMessage.remove();
                                                                                        }, 5000);
                                                                                    },
                                                                                    error: function(xhr, status, error) {
                                                                                        // Aksi jika terjadi kesalahan dalam permintaan AJAX
                                                                                        var errorMessage = document.createElement("div");
                                                                                        errorMessage.textContent = xhr.responseJSON.message;
                                                                                        document.getElementById("messageContainer").appendChild(
                                                                                            errorMessage);
                                                                                        setTimeout(function() {
                                                                                            errorMessage.remove();
                                                                                        }, 5000);
                                                                                    }
                                                                                });
                                                                                setTimeout(decodeQRCode, 50); // Melanjutkan pemindaian setelah 1 detik
                                                                            })
                                                                            .catch(err => {
                                                                                console.error(err);
                                                                                setTimeout(decodeQRCode, 50); // Melanjutkan pemindaian setelah 1 detik
                                                                            });
                                                                    }

                                                                    decodeQRCode(); // Memulai pemindaian QR kode

                                                                } else {
                                                                    alert("Camera not found!")
                                                                }
                                                            })
                                                            .catch(err => console.error(err));
                                                    }


                                                    if (navigator.mediaDevices) {


                                                        initScanner()


                                                    } else {
                                                        alert('Cannot access camera.');
                                                    }
                                                </script>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </section>

                            <a href="#" class="btn btn-warning">Perlu bantuan ?</a>
                        </div>
                    </div>
                </div>
                <!-- /.col-md-6 -->
            </div>
            <!-- /.row -->
        </div><!-- /.container-fluid -->
    </div>
    <!-- /.content -->
@endsection
